import React from "react"
import { authEndpoint, clientId, redirectUri, scopes } from "../auth/config"
import Modal from "react-bootstrap/Modal"
import Button from "react-bootstrap/Button"
import styled from "styled-components"

const ModalHeader = styled.div`
  text-align: center;
  font-size: 3rem;
`
const Body = styled.div`
width: 100vw;
height: 100vh;
display: flex;
justify-content: center;
align-items: center;
`

const CenteredDiv = styled.div`
color: white;
background-color: rgb(80, 80, 80);
`

const Login = () => {
  return (
    <Body>
      <CenteredDiv>
        <ModalHeader className="fredoka-font">BPM Playlist Maker</ModalHeader>
          <ModalBody>
            This app allows you to create playlists based on the BPM of songs to
              create perfectly customized running playlists.
          </ModalBody>
        </CenteredDiv>
    </Body>
  )

  return (
    <>
      <Modal show={true} centered className="myModal" animation={false}>
        <Modal.Body className="bg-dark myModal">
          <br />

          <br />
          <Button
            variant="success"
            href={`${authEndpoint}?client_id=${clientId}&redirect_uri=${redirectUri}&scope=${scopes.join(
              "%20"
            )}&response_type=token&show_dialog=false`}
          >
            Connect to Spotify
          </Button>
        </Modal.Body>
      </Modal>
    </>
  )
}

export default Login
